import React, { useRef } from 'react';
import { useEffect, useState } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
} from 'chart.js/auto';
import './App.css';
import { UserVelocity } from './component/Bar';
import { WorkBreakdownByIteration } from './component/WorkBreakdown';
import { HistoricalWorkBreakdown } from './component/HistoricalWorkBreakdown';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip
);

// interface graphInterface {
//   storyType: string,
//   totalStories: number
//   totalPoints: number
// }

interface graphInterface {
  iteration: string,
  totalStories: number
}



interface dataInterface {
  storyType: string,
  totalStories: number,
  totalPoints: number,
  iteration: string
}

function App() {

  const c: dataInterface = {
    iteration: '',
    storyType: '',
    totalStories: 0,
    totalPoints: 0,
  }
  const g: graphInterface = {
    iteration: 'string',
    totalStories: 0
  }
  // USER VELOCITY
  // const [data, setData] = useState<graphInterface[]>([c])
  // // const result = fetch('http://127.0.0.1:8000/breakdown/499934195612').then(resp => resp.json()).then(json => setData(json))
  // const [x, setX] = useState<number[]>(data.map(d => d.id))
  // const [y, setY] = useState<number[]>(data.map(d => d.velocity))

  // useEffect(() => {
  //   setX(data.map(d => d.id))
  //   setY(data.map(d => d.velocity))
  // }, [data])

  //WORK BREAKDOWN BY ITERATION
  // const [data, setData] = useState<graphInterface[]>([c]);
  // const [x, setX] = useState<string[]>(data.map(d => d.storyType))
  // const [y, setY] = useState<number[]>(data.map(d => d.totalStories))
  // const [y1, setY1] = useState<number[]>(data.map(d => d.totalPoints))

  // let arr1: number[] = [];
  // let arr3: number[] = [];
  // let arr2: string[] = [];

  // function handleClick() {
  //   fetch('http://127.0.0.1:8000/breakdown/499934195612', {
  //     method: 'GET',
  //     headers: {
  //       accept: 'application/json',
  //     },
  //   }).then(resp => resp.json()).then(json => setData(json));
  // }
  // useEffect(() => {

  //   data.map(d => {
  //     arr1.push(d.totalStories);
  //     arr3.push(d.totalPoints);
  //     arr2.push(d.storyType)
  //   })
  //   setY(arr1);
  //   setX(arr2);
  //   setY1(arr3);
  // }, [data])

  // WORK BREAKDOWN BY ITERATION
  const [data, setData] = useState<dataInterface[]>([c]);
  const [enhancement, setEnhancement] = useState<number[]>([])
  const [security, setSecurity] = useState<number[]>([])
  const [stabilization, setStabilization] = useState<number[]>([])
  const [feature, setFeature] = useState<number[]>([])
  const [defect, setDefect] = useState<number[]>([])
  const [iteration, setIteration] = useState<string[]>([])

  function uniq(a: string[]) {
    return Array.from(new Set(a));
  }

  function handleClick() {
    fetch('http://127.0.0.1:8000/breakdown', {
      method: 'GET',
      headers: {
        accept: 'application/json',
      },
    }).then(resp => resp.json()).then(json => setData(json));
  }
  useEffect(() => {
    let enh: number[] = [];
    let sec: number[] = [];
    let sta: number[] = [];
    let fea: number[] = [];
    let def: number[] = [];
    let iter: string[] = [];
    const map1 = new Map();
    data.map(d => {
      if (d.storyType === "DEFECT") {
        def.push(d.totalStories)
      }
      if (d.storyType === "STABILIZATION") {
        sta.push(d.totalStories)
      }
      if (d.storyType === "ENHANCEMENT") {
        enh.push(d.totalStories)
      }
      if (d.storyType === "FEATURE") {
        fea.push(d.totalStories)
      }
      if (d.storyType === "SECURITY") {
        sec.push(d.totalStories)
      }
      iter.push(d.iteration)
      if (!map1.has(d.iteration)) {
        map1.set(d.iteration, 0)
      }
      const newValue = d.totalStories + map1.get(d.iteration)
      map1.set(d.iteration, newValue);
    })
    setEnhancement(enh);
    setStabilization(sta);
    setSecurity(sec);
    setDefect(def);
    setFeature(fea);
    setIteration(uniq(iter));

  }, [data])

  return (
    <div className="App">
      Iteration Data
      <button onClick={() => handleClick()}>Get Data</button>
      <div>
        <HistoricalWorkBreakdown
          enhancements={enhancement}
          security={security}
          stabilization={stabilization}
          defect={defect}
          feature={feature}
          iteration={iteration}
        />
      </div>

    </div>
  );
}

export default App;
