import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
    Chart,
} from 'chart.js';
import { useEffect, useState } from 'react';
import { Line, Bar } from 'react-chartjs-2';

ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend
);

interface graphInterface {
    iteration: string,
    totalStories: number
}
interface allGraphData {
    iteration: string[],
    enhancements: number[],
    security: number[],
    feature: number[],
    defect: number[],
    stabilization: number[]
}

export function HistoricalWorkBreakdown(d: allGraphData) {
    console.log(d.iteration);
    const total = d.defect.map((x, index) => {
        return x + d.enhancements[index] + d.feature[index] + d.security[index] + d.stabilization[index];
    })

    const data = {
        labels: d.iteration,
        datasets: [
            {
                stack: "stack1",
                label: 'ENHANCEMENT',
                data: d.enhancements,
                // you can set indiviual colors for each bar
                backgroundColor: [
                    'rgba(201, 203, 207, 0.2)'
                ],
                borderColor: [
                    'rgb(201, 203, 207)'
                ],
                borderWidth: 2,
            },
            {
                stack: "stack1",
                label: 'SECURITY',
                data: d.security,
                backgroundColor: [
                    'rgba(255, 205, 86, 0.2)'
                ],
                borderColor: [
                    'rgb(255, 205, 86)'
                ],
                borderWidth: 2,
            },
            {
                stack: "stack1",
                label: 'STABILIZATION',
                data: d.stabilization,
                // you can set indiviual colors for each bar
                backgroundColor: [
                    'rgba(75, 192, 192, 0.2)'
                ],
                borderColor: [
                    'rgb(75, 192, 192)'
                ],
                borderWidth: 2,
            },
            {
                stack: "stack1",
                label: 'DEFECT',
                data: d.defect,
                // you can set indiviual colors for each bar
                backgroundColor: [
                    'rgba(153, 102, 255, 0.2)'
                ],
                borderColor: [
                    'rgb(54, 162, 235)'
                ],
                borderWidth: 2,
            },
            {
                stack: "stack1",
                label: 'FEATURE',
                data: d.feature,
                // you can set indiviual colors for each bar
                backgroundColor: [
                    'rgba(255, 159, 64, 0.2)'
                ],
                borderColor: [
                    'rgb(255, 159, 64)'
                ],
                borderWidth: 2,
            },
            {
                stack: "stack1",
                label: 'TOTAL',
                data: total,
                // you can set indiviual colors for each bar
                backgroundColor: [
                    'rgba(255, 99, 132, 0.2)'
                ],
                borderColor: [
                    'rgb(255, 99, 132)'
                ],
                borderWidth: 1,
            }
        ]
    };

    return <Bar data={data} />;
};
