import React, { useRef } from 'react';
import { useEffect, useState } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
} from 'chart.js';
import './App.css';
import { BarGraph } from './component/Bar';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip
);

interface graphInterface {
  id: number,
  velocity: number
}

function App() {

  const c: graphInterface = {
    id: 5,
    velocity: 4
  }

  const [data, setData] = useState<graphInterface[]>([c])
  const [x, setX] = useState<number[]>([1])
  const [y, setY] = useState<number[]>([1])

  useEffect(() => {
    const results = fetch('http://127.0.0.1:8000/velocity/users').then(resp => resp.json()).then(json => setData(json))

  })

  useEffect(() => {
    // const results = fetch('http://127.0.0.1:8000/velocity/users').then(resp => resp.json()).then(json => setData(json))
    setX(data.map(d => d.id));
    setY(data.map(d => d.velocity))

  }, [data])

  const ref = useRef()

  return (
    <div className="App">
      <div>
        <BarGraph
          ref={ref}
          x={x}
          y={y} />
      </div>

    </div>
  );
}

export default App;
